                    for(var i=0;i<messages.length;i++){
                        var curMsg = messages[i];
                        var code = curMsg.split(":")[0];
                        if(codes.indexOf(code)>=0){
                            errors[code] = curMsg.split(":")[1];
                            var messageToReplace = localizations.passwordRequirementsMessages && localizations.passwordRequirementsMessages[i18nmapper[code]];
                            if(messageToReplace)
                                messageItems.push(messageToReplace.replace("$$", errors[code]));
                            else{
                                messageItems.push(messages[i-1]);
                            }
                        }
                    }
                    var separator = localizations.passwordRequirementsMessages ? localizations.passwordRequirementsMessages.msgSeparator : "<br>";
                    return messageItems && messageItems.length > 0 ? messageItems.join(separator) : msg;
