                        $.growlUI(getLocalizationKey("NoFilesFoundGrowlTitle"), "<span class='nodataFoundErrorMsg'></span>" + getLocalizationKey("NoFilesFoundGrowlText") + crushFTPTools.textEncode(crushFTPTools.decodeURILocal(obj.path)) + "<br><small style='font-weight:normal;line-height:20px;'>"+info+"</small>", o.GrowlTimeout, "growlError", o.GrowlWithCloseButton, false, {
                            width: '450px'
                        });
