                // Adds line break in title of an item if it exceeds provided length
                function wbr(str, num) {
                    if (!regexpCache[num]) {
                        regexpCache[num] = RegExp("(\[^\s-].{" + num + "})(?=\[^\s-].)", "g");
                    }
                    return str.replace(regexpCache[num], "$1<br>");
                }

